package HW2P;

import java.util.Scanner;

public class HW2P11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scanner = new Scanner(System.in);
		int a = scanner.nextInt();
		int i = 0;
		int j = 0;
		
		while (a < j) {
			a++;
			j++;
			System.out.println(a + " X " + j + " = " + (i * j));
		}
			
	}

}

